<?phpnamespace app\controllers;use template\Recipe\RecipeTemplate;class CategoryController extends AppController{    public function reloadAction()    {        $id = empty($_GET['id']) ? null : $_GET['id'];        if ($id)        {            $recipe = \R::findAll('recipe','category_id = ?',[$id]);        }        else{            $recipe = \R::find('recipe','LIMIT 9');        }        $recipeTemplate = new RecipeTemplate();        $recipeTemplate->getTemplate($recipe);    }}