<?phpnamespace app\controllers;class HomeController extends AppController{    public function __construct($route)    {        parent::__construct($route);    }    public function indexAction()    {        $this->setMeta('Главная страница','Описание','Ключевики');        $recipe = \R::find('recipe','LIMIT 8');        $categories = \R::getAll('SELECT * FROM category');        $this->setData(compact('recipe','categories'));    }}