oup">                <div class="container">                    <label for="ingredient">Ингредиенты: </label>                </div>                <div class="container ingredients-list">                    <div class="container ingredients-child">                        <select id="ingredient-select" name="ingredient" class="form-select">                            <?php foreach($ingrediets as $ingrediet) : ?>                                <option data-unit="<?=$ingrediet['name'];?>" value="<?= $ingrediet['id'];?>"><?= $ingrediet['title']; ?></option>                            <?php endforeach; ?>                        </select>                        <label for="ingredient">Количество : </label>                        <input   type="number" min="1" id="quantity"  name="quantity" placeholder="Quantity" >                        <label id="ingredient-unit-name"><?= $ingrediet['name'] ?></label>                    </div>                </div>            </div></br>        <?php endif; ?>        <button type="submit" class="btn btn-primary ">Сохранить</button>    </form></div>