<?phpnamespace template\Recipe;class RecipeTemplate{    public $tpl;    public function __construct()    {        $this->tpl = __DIR__.'/recipe_tpl/recipe.php';    }    public function getHtmlTemplate($recipe)    {        ob_start();        require_once $this->tpl;        return ob_get_clean();    }    public function getTemplate($recipe)    {        require_once $this->tpl;        die();    }}