<?phpnamespace recipes;class App{    public static $app;    public function __construct()    {        $query = trim($_SERVER['QUERY_STRING'],'/');        self::$app = Registry::instance();        $this->setParams();        new ErrorHandler();    }    public function setParams()    {        $params  = require_once CONF.'/params.php';        if (!empty($params))        {            foreach ($params as $name => $value)            {                self::$app->setProperty($name, $value);            }        }    }}