<?phpnamespace recipes;class ErrorHandler{    public function __construct()    {        set_exception_handler([$this,'exeptionHandler']);    }    public function exeptionHandler($error)    {        $this->logErrors($error->getMessage(),$error->getFile(),$error->getLine());        $this->displayError($error->getMessage(),$error->getFile(),$error->getLine(),$error->getCode());    }    protected function logErrors($message = '', $file = '', $line = ''){        error_log("[" . date('Y-m-d H:i:s') . "] Текст ошибки: {$message} | Файл: {$file} | Строка: {$line}\n=================\n", 3, ROOT . '/errors.log');    }    protected function displayError($errstr, $errfile, $errline, $responce = 404){        http_response_code($responce);        require_once APP.'/views/errors/error.php';        die;    }}