<?phpnamespace recipes\base;abstract class Controller{    public $layout;    public $route;    public $controller;    public $view;    public $data = [];    public $meta = ['title' => '','desc' => '','keywords' => ''];    public function __construct($route)    {        $this->route = $route;        $this->controller = $route['controller'];        $this->view = $route['action'];    }    public function setData($data)    {        $this->data = $data;    }    public function getView()    {        $view = new View($this->route,$this->layout,$this->meta);        $view->setLayout($this->layout);        $view->render($this->data);    }    public function setMeta($title,$desc,$keywords)    {        $this->meta['title'] = $title;        $this->meta['desc'] = $desc;        $this->meta['keywords'] = $keywords;    }    public function loadView($view,$vars = [])    {        extract($vars);        require_once APP . "/views/{$this->controller}/{$view}.php";        die();    }}